class Solution {
public:
    int eraseOverlapIntervals(vector<vector<int>>& I) {
        sort(I.begin(), I.end());

        int count = 0;
        int prevStart = I[0][0], prevEnd = I[0][1];

        for(int i=1; i<I.size(); i++){
            if(I[i][0] >= prevEnd){
                prevStart = I[i][0], prevEnd = I[i][1];
            }
            else if(I[i][1] >= prevEnd){
                count++;
            }
            else if(I[i][1] < prevEnd){
                prevStart = I[i][0], prevEnd = I[i][1];
                count++;
            }
        }

        return  count;                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                                             
    }
};